import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const QueueForm = ({ onAddToQueue, queue, error }) => {
  const { restaurantId } = useParams(); // 获取餐厅 ID
  const [partySize, setPartySize] = useState(1);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [queueNumber, setQueueNumber] = useState(null);
  const [formError, setFormError] = useState(null);
  const [waitingTimeRange, setWaitingTimeRange] = useState("0 - 0 mins"); // 等待时间区间

  // 初始化加载队列
  useEffect(() => {
    const fetchQueueData = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/${restaurantId}/queue`);
        const data = await response.json();

        if (data.success) {
          onAddToQueue(data.queue); // 更新队列数据
          setWaitingTimeRange(data.waitingTimeRange); // 设置等待时间区间
        } else {
          setFormError(data.message || "Failed to fetch queue data.");
        }
      } catch (err) {
        setFormError("Error connecting to the server.");
      }
    };

    fetchQueueData();
  }, [restaurantId, onAddToQueue]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormError(null);

    if (!name.trim() || !phone.trim()) {
      setFormError("Please fill in all fields.");
      return;
    }

    if (!/^\d{10}$/.test(phone)) {
      setFormError("Phone number must be exactly 10 digits.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:5001/api/${restaurantId}/queue`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, phone, partySize }),
      });
      const data = await response.json();

      if (response.ok) {
        setQueueNumber(data.queueNumber); // 显示队列号
        setFormError(null);

        // 重新加载队列数据和等待时间
        const queueResponse = await fetch(`http://localhost:5001/api/${restaurantId}/queue`);
        const queueData = await queueResponse.json();
        onAddToQueue(queueData.queue);
        setWaitingTimeRange(queueData.waitingTimeRange);
      } else {
        setFormError(data.error || "Failed to join the queue.");
      }
    } catch {
      setFormError("Error connecting to the server.");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Join the Waitlist for {restaurantId}</h2>

      {/* 表单部分 */}
      <form onSubmit={handleSubmit} style={{ marginTop: "20px" }}>
        <div style={{ marginBottom: "20px" }}>
          <label htmlFor="name" style={{ display: "block", fontWeight: "bold", marginBottom: "5px" }}>
            Your Name:
          </label>
          <input
            id="name"
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={{ padding: "10px", width: "300px" }}
          />
        </div>
        <div style={{ marginBottom: "20px" }}>
          <label htmlFor="phone" style={{ display: "block", fontWeight: "bold", marginBottom: "5px" }}>
            Phone Number:
          </label>
          <input
            id="phone"
            type="text"
            placeholder="Enter your phone number"
            value={phone}
            maxLength="10"
            onChange={(e) => setPhone(e.target.value.replace(/\D/g, ""))}
            style={{ padding: "10px", width: "300px" }}
          />
        </div>
        <div style={{ marginBottom: "20px" }}>
          <label htmlFor="partySize" style={{ display: "block", fontWeight: "bold", marginBottom: "5px" }}>
            Party Size:
          </label>
          <input
            id="partySize"
            type="number"
            placeholder="Enter party size"
            value={partySize}
            onChange={(e) => setPartySize(e.target.value)}
            style={{ padding: "10px", width: "300px" }}
          />
        </div>
        <button
          type="submit"
          style={{
            padding: "10px 20px",
            backgroundColor: "#007bff",
            color: "#fff",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
          }}
        >
          Get Queue Number
        </button>
      </form>

      {/* 错误信息显示 */}
      {formError && <p style={{ color: "red", marginTop: "10px" }}>{formError}</p>}

      {/* 队列号显示 */}
      {queueNumber && (
        <div style={{ marginTop: "20px" }}>
          <h3>Your Queue Number</h3>
          <p style={{ fontSize: "24px", color: "#007bff" }}>{queueNumber}</p>
          <p style={{ color: "green", marginTop: "10px" }}>Thank you for joining the waitlist.</p>
        </div>
      )}

      {/* 动态等待时间 */}
      <p style={{ marginTop: "20px", fontWeight: "bold", color: "green" }}>Waiting time: {waitingTimeRange}</p>
    </div>
  );
};

export default QueueForm;
