import React from "react";

const RestaurantSelector = () => {
  const restaurants = [
    { id: "restaurant_waitlist_Walnut", name: "Walnut Restaurant" },
    { id: "restaurant_waitlist_Atlantic", name: "Atlantic Restaurant" },
    { id: "restaurant_waitlist_MontereyPark", name: "Monterey Park Restaurant" },
  ];

  // 定义 frontend 和 staff_frontend 的地址
  const FRONTEND_URL = "http://localhost:3000"; // frontend 运行地址
  const STAFF_FRONTEND_URL = "http://localhost:3001"; // staff_frontend 运行地址

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Select a Restaurant</h1>
      {restaurants.map((restaurant) => (
        <div key={restaurant.id} style={{ marginBottom: "20px" }}>
          <h3>{restaurant.name}</h3>
          {/* Customer Access 按钮跳转到 frontend 的客户页面 */}
          <button
            onClick={() =>
              (window.location.href = `${FRONTEND_URL}/customer/${restaurant.id}`)
            }
            style={{ padding: "10px 20px", marginRight: "10px" }}
          >
            Customer Access
          </button>
          {/* Staff Access 按钮跳转到 staff_frontend 的员工页面 */}
          <button
            onClick={() =>
              (window.location.href = `${STAFF_FRONTEND_URL}/staff/${restaurant.id}`)
            }
            style={{ padding: "10px 20px" }}
          >
            Staff Access
          </button>
        </div>
      ))}
    </div>
  );
};

export default RestaurantSelector;
