import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const QueueForm = ({ onAddToQueue, queue, error }) => {
  const { restaurantId } = useParams(); // 获取餐厅 ID
  const [partySize, setPartySize] = useState(1);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [queueNumber, setQueueNumber] = useState(null);
  const [formError, setFormError] = useState(null);

  // 初始化加载队列
  useEffect(() => {
    onAddToQueue(restaurantId); // 保持原有逻辑：加载队列数据
  }, [restaurantId]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name.trim() || !phone.trim()) {
      setFormError("Please fill in all fields.");
      return;
    }

    const phoneRegex = /^\d{10}$/;
    if (!phoneRegex.test(phone)) {
      setFormError("Phone number must be exactly 10 digits.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:5001/api/${restaurantId}/queue`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, phone, partySize }),
      });
      const data = await response.json();

      if (response.ok) {
        setQueueNumber(data.queueNumber); // 显示队列号
        setFormError(null);
        onAddToQueue(restaurantId); // 刷新队列数据
      } else {
        setFormError(data.error || "Failed to join the queue.");
      }
    } catch (err) {
      setFormError("Error connecting to the server.");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Join the Waitlist for {restaurantId}</h2>

      {/* 表单部分：保留原有逻辑 */}
      <form onSubmit={handleSubmit} style={{ marginTop: "20px" }}>
        <div style={{ marginBottom: "20px" }}>
          <label htmlFor="name" style={{ display: "block", marginBottom: "5px", fontWeight: "bold" }}>
            Your Name:
          </label>
          <input
            id="name"
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={{ padding: "10px", width: "300px" }}
          />
        </div>

        <div style={{ marginBottom: "20px" }}>
          <label
            htmlFor="phone"
            style={{ display: "block", marginBottom: "5px", fontWeight: "bold" }}
          >
            Phone Number:
          </label>
          <input
            id="phone"
            type="text"
            placeholder="Enter your phone number"
            value={phone}
            maxLength="10" // 限制最大输入长度为 10
            onChange={(e) => {
              // 仅允许输入数字，并限制为 10 位
              const input = e.target.value.replace(/\D/g, ""); // 去除非数字字符
              setPhone(input.slice(0, 10)); // 确保长度不超过 10
            }}
            style={{ padding: "10px", width: "300px" }}
          />
        </div>


        <div style={{ marginBottom: "20px" }}>
          <label htmlFor="partySize" style={{ display: "block", marginBottom: "5px", fontWeight: "bold" }}>
            Party Size:
          </label>
          <input
            id="partySize"
            type="number"
            placeholder="Enter party size"
            value={partySize}
            onChange={(e) => setPartySize(e.target.value)}
            style={{ padding: "10px", width: "300px" }}
          />
        </div>

        <button
          type="submit"
          style={{
            padding: "10px 20px",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
          }}
        >
          Get Queue Number
        </button>
      </form>

      {/* 错误信息显示 */}
      {formError && <p style={{ color: "red" }}>{formError}</p>}

      {/* 队列号显示 */}
      {queueNumber && (
        <div style={{ marginTop: "20px" }}>
          <h3>Your Queue Number</h3>
          <p style={{ fontSize: "24px", fontWeight: "bold", color: "#007bff" }}>
            <text>Thank you for joining the Waitlist!</text>
            {queueNumber}
          </p>
        </div>
      )}

      {/* 队列列表显示 */}
      <div style={{ marginTop: "40px", textAlign: "left", padding: "10px" }}>
        <h3>Current Queue</h3>
        {error && <p style={{ color: "red" }}>{error}</p>}
        {!error && queue.length === 0 && <p>No customers in the queue.</p>}
        <ul>
          {queue.map((entry) => (
            <li key={entry._id}>
              <strong>{entry.queueNumber}:</strong> {entry.name} (Party Size: {entry.partySize})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default QueueForm;
