import React, { useState } from "react";
import { useParams } from "react-router-dom";
import QueueList from "./QueueList";
import TableConfigForm from "./TableConfigForm";

const StaffDashboard = () => {
  const { restaurantId } = useParams(); // 从 URL 动态获取 restaurantId
  const [showConfigForm, setShowConfigForm] = useState(false); // 控制是否显示配置表单

  // 打开和关闭配置表单
  const handleOpenConfigForm = () => setShowConfigForm(true);
  const handleCloseConfigForm = () => setShowConfigForm(false);

  // 如果没有 restaurantId，显示错误信息
  if (!restaurantId) {
    return <p style={{ color: "red" }}>Restaurant ID is required to view this dashboard.</p>;
  }

  return (
    <div style={{ padding: "20px" }}>
      <h2>Queue Management for {restaurantId}</h2>

      {/* 显示队列列表 */}
      <QueueList restaurantId={restaurantId} />

      {/* 按钮用于显示桌子配置表单 */}
      <div style={{ marginTop: "20px" }}>
        <button
          onClick={handleOpenConfigForm}
          style={{
            padding: "10px 15px",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Update Table Config
        </button>
      </div>

      {/* 配置表单 */}
      {showConfigForm && (
        <TableConfigForm
          restaurantId={restaurantId}
          onClose={handleCloseConfigForm}
          onUpdate={() => {
            console.log("Table configuration updated.");
            // 需要时可以重新加载队列或其他逻辑
          }}
        />
      )}
    </div>
  );
};

export default StaffDashboard;
