import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const QueueList = () => {
  const { restaurantId } = useParams(); // Get restaurantId from the URL
  const [queue, setQueue] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [searchType, setSearchType] = useState("name");
  const [searchTerm, setSearchTerm] = useState("");
  const [tableConfig, setTableConfig] = useState(null);

  // Fetch queue data for the specific restaurant
  const fetchQueue = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/api/${restaurantId}/queue`);
      if (response.status === 200) {
        setQueue(response.data);
        setError(null);
      } else {
        console.error("Failed to fetch queue:", response.statusText);
        setError("Failed to fetch queue data.");
      }
    } catch (err) {
      console.error("Error fetching queue:", err);
      setError("Error fetching queue data.");
    }
  };


  // Delete a queue item
  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this item?")) {
      try {
        await axios.delete(
          `http://localhost:5001/api/${restaurantId}/queue/${id}`
        );
        fetchQueue(); // Refresh the list
      } catch (error) {
        console.error("Error deleting queue item:", error);
        alert("Failed to delete the item. Please try again.");
      }
    }
  };

  // Notify a customer
  const handleNotify = async (id) => {
    try {
      await axios.patch(
        `http://localhost:5001/api/${restaurantId}/queue/${id}/notify`
      );
      fetchQueue(); // Refresh the list
    } catch (error) {
      console.error("Error notifying customer:", error);
      alert("Failed to notify the customer. Please try again.");
    }
  };

  // Handle search input changes
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Perform a search
  const handleSearch = () => {
    const searchParams = {};
    if (searchType === "name") {
      searchParams.name = searchTerm;
    } else if (searchType === "queueNumber") {
      searchParams.queueNumber = searchTerm;
    }
    fetchQueue(searchParams);
  };

  // Fetch table configuration
  const fetchTableConfig = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/api/${restaurantId}/table-config`);
      if (response.data && response.data.success) {
        setTableConfig(response.data.tableConfig);
      } else {
        console.error("Error fetching table configuration:", response.data.error);
      }
    } catch (error) {
      console.error("Error fetching table configuration:", error);
    }
  };

  // Fetch queue and table config data initially
  useEffect(() => {
    if (restaurantId) {
      fetchQueue();
      fetchTableConfig();
    }
  }, [restaurantId]); // Dependency ensures it re-fetches if restaurantId changes

  if (isLoading) return <p>Loading...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div>
      <h2>Queue List for {restaurantId}</h2>
      {queue && queue.length > 0 ? (
        <p>{queue.length} customers in the queue</p>
      ) : (
        <p>No customers in the queue</p>
      )}

      {tableConfig ? (
        <div>
          <h3>Table Configuration</h3>
          <p>Total Large Tables: {tableConfig.totalLarge}</p>
          <p>Total Medium Tables: {tableConfig.totalMedium}</p>
          <p>Total Small Tables: {tableConfig.totalSmall}</p>
          <p>Large Table Avg. Time: {tableConfig.averageTimeLarge} mins</p>
          <p>Medium Table Avg. Time: {tableConfig.averageTimeMedium} mins</p>
          <p>Small Table Avg. Time: {tableConfig.averageTimeSmall} mins</p>
        </div>
      ) : (
        <p>Loading Table Config...</p>
      )}

      {/* Search functionality */}
      <div style={{ marginBottom: "20px" }}>
        <label>
          Search by:{" "}
          <select
            value={searchType}
            onChange={(e) => setSearchType(e.target.value)}
            style={{ marginRight: "10px" }}
          >
            <option value="name">Name</option>
            <option value="queueNumber">Queue Number</option>
          </select>
        </label>
        <input
          type="text"
          placeholder={`Search by ${searchType}`}
          value={searchTerm}
          onChange={handleSearchChange}
          style={{ padding: "5px", marginRight: "10px" }}
        />
        <button onClick={handleSearch} style={{ padding: "5px 10px" }}>
          Search
        </button>
      </div>

      {/* Queue list */}
      {queue.length > 0 && (
        <table
          border="1"
          cellPadding="10"
          style={{
            width: "100%",
            marginTop: "20px",
            borderCollapse: "collapse",
          }}
        >
          <thead>
            <tr>
              <th>Queue Number</th>
              <th>Name</th>
              <th>Phone</th>
              <th>Party Size</th>
              <th>Notified</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {queue.map((item) => (
              <tr key={item._id}>
                <td>{item.queueNumber}</td>
                <td>{item.name}</td>
                <td>{item.phone}</td>
                <td>{item.partySize}</td>
                <td>{item.notified ? "Yes" : "No"}</td>
                <td>
                  <button
                    onClick={() => handleNotify(item._id)}
                    disabled={item.notified}
                    style={{
                      backgroundColor: item.notified ? "#ccc" : "#28a745",
                      color: "white",
                      border: "none",
                      padding: "5px 10px",
                      cursor: item.notified ? "not-allowed" : "pointer",
                      marginRight: "10px",
                    }}
                  >
                    Notify
                  </button>
                  <button
                    onClick={() => handleDelete(item._id)}
                    style={{
                      backgroundColor: "#d9534f",
                      color: "white",
                      border: "none",
                      padding: "5px 10px",
                      cursor: "pointer",
                    }}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default QueueList;
