import React, { useState, useEffect } from "react";
import axios from "axios";

const TableConfigForm = ({ restaurantId, tableConfig, onUpdate }) => {
  const [totalLarge, setTotalLarge] = useState(tableConfig.totalLarge || 5);
  const [totalMedium, setTotalMedium] = useState(tableConfig.totalMedium || 10);
  const [totalSmall, setTotalSmall] = useState(tableConfig.totalSmall || 15);
  const [averageTimeLarge, setAverageTimeLarge] = useState(tableConfig.averageTimeLarge || 90);
  const [averageTimeMedium, setAverageTimeMedium] = useState(tableConfig.averageTimeMedium || 60);
  const [averageTimeSmall, setAverageTimeSmall] = useState(tableConfig.averageTimeSmall || 30);

  useEffect(() => {
    setTotalLarge(tableConfig.totalLarge || 5);
    setTotalMedium(tableConfig.totalMedium || 10);
    setTotalSmall(tableConfig.totalSmall || 15);
    setAverageTimeLarge(tableConfig.averageTimeLarge || 90);
    setAverageTimeMedium(tableConfig.averageTimeMedium || 60);
    setAverageTimeSmall(tableConfig.averageTimeSmall || 30);
  }, [tableConfig]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        `http://localhost:5001/api/${restaurantId}/table-config`,
        {
          totalLarge,
          totalMedium,
          totalSmall,
          averageTimeLarge,
          averageTimeMedium,
          averageTimeSmall,
        }
      );
      if (response.data && response.data.success) {
        alert("Table configuration updated successfully!");
        onUpdate();
      } else {
        console.error("Unexpected response:", response.data);
        alert("Failed to update table configuration. Please check the server.");
      }
    } catch (error) {
      console.error("Error updating table configuration:", error);
      alert("Failed to update table configuration. Please try again.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Total Large Tables:</label>
        <input type="number" value={totalLarge} onChange={(e) => setTotalLarge(e.target.value)} />
      </div>
      <div>
        <label>Total Medium Tables:</label>
        <input type="number" value={totalMedium} onChange={(e) => setTotalMedium(e.target.value)} />
      </div>
      <div>
        <label>Total Small Tables:</label>
        <input type="number" value={totalSmall} onChange={(e) => setTotalSmall(e.target.value)} />
      </div>
      <div>
        <label>Large Table Avg. Time (mins):</label>
        <input type="number" value={averageTimeLarge} onChange={(e) => setAverageTimeLarge(e.target.value)} />
      </div>
      <div>
        <label>Medium Table Avg. Time (mins):</label>
        <input type="number" value={averageTimeMedium} onChange={(e) => setAverageTimeMedium(e.target.value)} />
      </div>
      <div>
        <label>Small Table Avg. Time (mins):</label>
        <input type="number" value={averageTimeSmall} onChange={(e) => setAverageTimeSmall(e.target.value)} />
      </div>
      <button type="submit">Save</button>
    </form>
  );
};

export default TableConfigForm;
