import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const QueueList = () => {
  const { restaurantId } = useParams(); // 获取餐厅 ID
  const [queue, setQueue] = useState([]);
  const [error, setError] = useState(null);
  const [searchType, setSearchType] = useState("name");
  const [searchTerm, setSearchTerm] = useState("");

  // 获取队列数据
  const fetchQueue = async (searchParams = {}) => {
    try {
      const queryString = new URLSearchParams(searchParams).toString();
      const url = `http://localhost:5001/api/${restaurantId}/queue?${queryString}`;
      console.log(`Fetching queue data from: ${url}`);
      const response = await axios.get(url);

      if (response.data && response.data.success) {
        console.log("Queue data fetched:", response.data.queue);
        setQueue(response.data.queue);
      } else {
        console.error("Failed to fetch queue data:", response.data.error);
        setQueue([]);
      }
    } catch (error) {
      console.error("Error fetching queue:", error);
      setQueue([]);
    }
  };

  // 删除队列项
  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this item?")) {
      try {
        await axios.delete(
          `http://localhost:5001/api/${restaurantId}/queue/${id}`
        );
        fetchQueue(); // 刷新队列
      } catch (error) {
        console.error("Error deleting queue item:", error);
        alert("Failed to delete the item. Please try again.");
      }
    }
  };

  // 通知客户
  const handleNotify = async (id) => {
    try {
      await axios.patch(
        `http://localhost:5001/api/${restaurantId}/queue/${id}/notify`
      );
      fetchQueue(); // 刷新队列
    } catch (error) {
      console.error("Error notifying customer:", error);
      alert("Failed to notify the customer. Please try again.");
    }
  };

  // 处理搜索输入变化
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // 执行搜索
  const handleSearch = () => {
    const searchParams = {};
    if (searchType === "name") {
      searchParams.name = searchTerm;
    } else if (searchType === "queueNumber") {
      searchParams.queueNumber = searchTerm;
    }
    console.log("Search params:", searchParams);
    fetchQueue(searchParams);
  };

  // 初始化加载队列数据
  useEffect(() => {
    if (restaurantId) {
      fetchQueue(); // 初始化时获取数据
    }
  }, [restaurantId]);

  return (
    <div>
      <h2>Queue List for {restaurantId}</h2>

      {/* 搜索框 */}
      <div style={{ marginBottom: "20px" }}>
        <label>
          Search by:{" "}
          <select
            value={searchType}
            onChange={(e) => setSearchType(e.target.value)}
            style={{ marginRight: "10px" }}
          >
            <option value="name">Name</option>
            <option value="queueNumber">Queue Number</option>
          </select>
        </label>
        <input
          type="text"
          placeholder={`Search by ${searchType}`}
          value={searchTerm}
          onChange={handleSearchChange}
          style={{ padding: "5px", marginRight: "10px" }}
        />
        <button onClick={handleSearch} style={{ padding: "5px 10px" }}>
          Search
        </button>
      </div>

      {/* 错误信息 */}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* 队列表格 */}
      {queue.length > 0 ? (
        <div
          style={{
            maxHeight: "300px", // 最大高度
            overflowY: "auto", // 垂直滚动
            border: "1px solid #ddd", // 边框
            borderRadius: "5px", // 圆角
            padding: "10px", // 内边距
            backgroundColor: "#f9f9f9", // 背景颜色
            boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)", // 阴影效果
          }}
        >
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th style={{ borderBottom: "1px solid #ddd", padding: "8px" }}>Queue Number</th>
                <th style={{ borderBottom: "1px solid #ddd", padding: "8px" }}>Name</th>
                <th style={{ borderBottom: "1px solid #ddd", padding: "8px" }}>Phone</th>
                <th style={{ borderBottom: "1px solid #ddd", padding: "8px" }}>Party Size</th>
                <th style={{ borderBottom: "1px solid #ddd", padding: "8px" }}>Notified</th>
                <th style={{ borderBottom: "1px solid #ddd", padding: "8px" }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {queue.map((item) => (
                <tr key={item._id}>
                  <td style={{ padding: "8px", borderBottom: "1px solid #ddd" }}>{item.queueNumber}</td>
                  <td style={{ padding: "8px", borderBottom: "1px solid #ddd" }}>{item.name}</td>
                  <td style={{ padding: "8px", borderBottom: "1px solid #ddd" }}>{item.phone}</td>
                  <td style={{ padding: "8px", borderBottom: "1px solid #ddd" }}>{item.partySize}</td>
                  <td style={{ padding: "8px", borderBottom: "1px solid #ddd" }}>
                    {item.notified ? "Yes" : "No"}
                  </td>
                  <td style={{ padding: "8px", borderBottom: "1px solid #ddd" }}>
                    <button
                      onClick={() => handleNotify(item._id)}
                      disabled={item.notified}
                      style={{
                        backgroundColor: item.notified ? "#ccc" : "#28a745",
                        color: "white",
                        padding: "5px 10px",
                        marginRight: "10px",
                      }}
                    >
                      Notify
                    </button>
                    <button
                      onClick={() => handleDelete(item._id)}
                      style={{
                        backgroundColor: "#d9534f",
                        color: "white",
                        padding: "5px 10px",
                      }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>No customers in the queue</p>
      )}
    </div>
  );
};

export default QueueList;
