import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const QueueList = () => {
  const { restaurantId } = useParams(); // Get restaurantId from the URL
  const [queue, setQueue] = useState([]);
  const [error, setError] = useState(null);
  const [searchType, setSearchType] = useState("name");
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResult, setSearchResult] = useState(null); // 存储搜索结果
  const [isSearching, setIsSearching] = useState(false); // 是否处于搜索模式

  // Fetch queue data for the specific restaurant
  const fetchQueue = async (searchParams = {}) => {
    try {
      const queryString = new URLSearchParams(searchParams).toString();
      const url = `http://localhost:5001/api/${restaurantId}/queue?`;
      const response = await axios.get(url);

      if (response.data && response.data.success) {
        console.log("Filtered queue data:", response.data.queue);
        setSearchResult(response.data.queue); // 更新搜索结果
      } else {
        console.error("Failed to fetch queue data:", response.data.error);
        setSearchResult([]); // 空搜索结果
      }
    } catch (error) {
      console.error("Error fetching queue:", error);
      setSearchResult([]); // 搜索失败时
    }
  };

  // Delete a queue item
  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this item?")) {
      try {
        await axios.delete(
          `http://localhost:5001/api/${restaurantId}/queue/${id}`
        );
        fetchQueue(); // Refresh the list
      } catch (error) {
        console.error("Error deleting queue item:", error);
        alert("Failed to delete the item. Please try again.");
      }
    }
  };

  // Notify a customer
  const handleNotify = async (id) => {
    try {
      await axios.patch(
        `http://localhost:5001/api/${restaurantId}/queue/${id}/notify`
      );
      fetchQueue(); // Refresh the list
    } catch (error) {
      console.error("Error notifying customer:", error);
      alert("Failed to notify the customer. Please try again.");
    }
  };

  // Handle search input changes
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Perform a search
  const handleSearch = () => {
    const searchParams = {};
    if (searchType === "name" && searchTerm.trim()) {
      searchParams.name = searchTerm.trim();
    } else if (searchType === "queueNumber" && searchTerm.trim()) {
      searchParams.queueNumber = searchTerm.trim();
    }

    fetchQueue(searchParams);
  };


  // Fetch queue data initially
  useEffect(() => {
    if (restaurantId) {
      fetchQueue(); // Fetch initial data without search filters
    }
  }, [restaurantId]);

  return (
    <div>
      <h2>Queue List for {restaurantId}</h2>

      {/* 搜索框 */}
      <div style={{ marginBottom: "20px" }}>
        <label>
          Search by:{" "}
          <select
            value={searchType}
            onChange={(e) => setSearchType(e.target.value)}
            style={{ marginRight: "10px" }}
          >
            <option value="name">Name</option>
            <option value="queueNumber">Queue Number</option>
          </select>
        </label>
        <input
          type="text"
          placeholder={`Search by ${searchType}`}
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: "5px", marginRight: "10px" }}
        />
        <button onClick={handleSearch} style={{ padding: "5px 10px" }}>
          Search
        </button>
      </div>

      {/* 搜索结果小窗口 */}
      {isSearching && searchResult && (
        <div style={{ border: "1px solid #ccc", padding: "10px", marginTop: "10px" }}>
          <h3>Search Results</h3>
          {searchResult.length > 0 ? (
            <ul>
              {searchResult.map((item) => (
                <li key={item._id}>
                  {item.queueNumber} - {item.name} ({item.phone})
                </li>
              ))}
            </ul>
          ) : (
            <p>No results found.</p>
          )}
        </div>
      )}

      {/* 主表格 */}
      {!isSearching && queue.length > 0 ? (
        <table border="1" cellPadding="10" style={{ width: "100%", marginTop: "20px" }}>
          <thead>
            <tr>
              <th>Queue Number</th>
              <th>Name</th>
              <th>Phone</th>
              <th>Party Size</th>
              <th>Notified</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {queue.map((item) => (
              <tr key={item._id}>
                <td>{item.queueNumber}</td>
                <td>{item.name}</td>
                <td>{item.phone}</td>
                <td>{item.partySize}</td>
                <td>{item.notified ? "Yes" : "No"}</td>
                <td>
                  <button
                    onClick={() => handleNotify(item._id)}
                    disabled={item.notified}
                    style={{
                      backgroundColor: item.notified ? "#ccc" : "#28a745",
                      color: "white",
                      padding: "5px 10px",
                    }}
                  >
                    Notify
                  </button>
                  <button
                    onClick={() => handleDelete(item._id)}
                    style={{
                      backgroundColor: "#d9534f",
                      color: "white",
                      padding: "5px 10px",
                    }}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        !isSearching && <p>No customers in the queue</p>
      )}
    </div>
  );






};

export default QueueList;
