import React from "react";

const RestaurantSelector = () => {
  const restaurants = [
    { id: "Walnut", name: "Walnut Restaurant" },
    { id: "Atlantic", name: "Atlantic Restaurant" },
    { id: "MontereyPark", name: "Monterey Park Restaurant" },
  ];

  // Define frontend and staff_frontend URLs
  const FRONTEND_URL = "http://localhost:3000"; // frontend running address
  const STAFF_FRONTEND_URL = "http://localhost:3001"; // staff_frontend running address

  // Navigate to staff page
  const handleStaffAccess = (restaurantId) => {
    window.location.href = `${STAFF_FRONTEND_URL}/staff/${restaurantId}`;
  };

  // Navigate to customer page
  const handleCustomerAccess = (restaurantId) => {
    window.location.href = `${FRONTEND_URL}/customer/${restaurantId}`;
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Select a Restaurant</h1>
      {restaurants.map((restaurant) => (
        <div key={restaurant.id} style={{ marginBottom: "20px" }}>
          <h3>{restaurant.name}</h3>
          {/* Customer Access Button */}
          <button
            onClick={() => handleCustomerAccess(restaurant.id)}
            style={{
              padding: "10px 20px",
              marginRight: "10px",
              backgroundColor: "#007bff",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Customer Access
          </button>
          {/* Staff Access Button */}
          <button
            onClick={() => handleStaffAccess(restaurant.id)}
            style={{
              padding: "10px 20px",
              backgroundColor: "#28a745",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Staff Access
          </button>
        </div>
      ))}
    </div>
  );
};

export default RestaurantSelector;
