import React, { useState, useEffect } from "react";
import axios from "axios";

const TableConfigForm = ({ restaurantId }) => {
  const [tableConfig, setTableConfig] = useState({
    totalLarge: 0,
    totalMedium: 0,
    totalSmall: 0,
    averageTimeLarge: 0,
    averageTimeMedium: 0,
    averageTimeSmall: 0,
  });

  const [loading, setLoading] = useState(true); // Add a loading state
  const [error, setError] = useState(null); // Add error state

  // Fetch table configuration from the backend
  useEffect(() => {
    const fetchTableConfig = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5001/api/${restaurantId}/table-config`
        );
        if (response.data && response.data.success) {
          setTableConfig(response.data.tableConfig);
        } else {
          throw new Error("Failed to fetch table configuration.");
        }
      } catch (err) {
        console.error("Error fetching table config:", err);
        setError("Failed to load table configuration.");
      } finally {
        setLoading(false);
      }
    };

    fetchTableConfig();
  }, [restaurantId]);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        `http://localhost:5001/api/${restaurantId}/table-config`,
        tableConfig
      );
      if (response.data.success) {
        alert("Table configuration updated successfully!");
      } else {
        alert("Failed to update table configuration. Please try again.");
      }
    } catch (err) {
      console.error("Error updating table config:", err);
      alert("Failed to update table configuration.");
    }
  };

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setTableConfig((prevConfig) => ({
      ...prevConfig,
      [name]: Number(value),
    }));
  };

  // Loading or error state
  if (loading) return <p>Loading table configuration...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <form onSubmit={handleSubmit}>
      <h3>Update Table Configuration</h3>
      <div>
        <label>Total Large Tables:</label>
        <input
          type="number"
          name="totalLarge"
          value={tableConfig.totalLarge}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Total Medium Tables:</label>
        <input
          type="number"
          name="totalMedium"
          value={tableConfig.totalMedium}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Total Small Tables:</label>
        <input
          type="number"
          name="totalSmall"
          value={tableConfig.totalSmall}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Average Time for Large Tables:</label>
        <input
          type="number"
          name="averageTimeLarge"
          value={tableConfig.averageTimeLarge}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Average Time for Medium Tables:</label>
        <input
          type="number"
          name="averageTimeMedium"
          value={tableConfig.averageTimeMedium}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Average Time for Small Tables:</label>
        <input
          type="number"
          name="averageTimeSmall"
          value={tableConfig.averageTimeSmall}
          onChange={handleChange}
        />
      </div>
      <button type="submit">Save Configuration</button>
    </form>
  );
};

export default TableConfigForm;
